import pdfMake from "pdfmake/build/pdfmake";
import pdfFonts from "pdfmake/build/vfs_fonts";

const impostoPDF = (data) => {
  if (data == undefined) {
    return(alert(`Sem imposto de renda de ${(new Date()).getFullYear()}.`))
  } else {
    pdfMake.vfs = pdfFonts.pdfMake.vfs;
  
    function prepareHeaders() {
      const nameArray = [data.name];
      const finalName = [];
  
      data.dependentes.map((dependente) => {
        nameArray.push(dependente.name);
      });
  
      nameArray.map((name) => {
        finalName.push({ text: name, style: "tableHeader" });
      });
  
      return finalName;
    }
  
    function prepareRows() {
      const finalRows = [];
      const dependentesArray = [];
  
      data.dependentes.map((dep) => {
        let temp = dep.impostoDeRenda[0];
        delete temp.ano;
        dependentesArray.push(Object.values(temp));
      });
  
      let somas = [];
  
      for (let i = 0; i < dependentesArray.length + 1; i++) {
        somas.push(0);
      }
  
      const meses = [
        "Janeiro",
        "Fevereiro",
        "Março",
        "Abril",
        "Maio",
        "Junho",
        "Julho",
        "Agosto",
        "Setembro",
        "Outubro",
        "Novembro",
        "Dezembro",
      ];
  
      const impostoAssociado = Object.values(data);
      delete impostoAssociado[1]["ano"];
      delete impostoAssociado[1]["idUser"];
      delete impostoAssociado[1]["_id"];
      delete impostoAssociado[1]["__v"];
  
      for (let i = 0; i < meses.length; i++) {
        let temp = [];
        let valorImposto = impostoAssociado[2][meses[i].toLowerCase().replace("ç","c")]
        somas[0] += valorImposto;
        temp.push(meses[i]);
  
        if (valorImposto == 0) {
          let zero = 0;
          temp.push(
            zero.toLocaleString("pt-br", {
              style: "currency",
              currency: "BRL",
            })
          );
        } 
        else {
          temp.push(
            valorImposto.toLocaleString("pt-br", {
              style: "currency",
              currency: "BRL",
            })
          );
        }
        for (let k = 0; k < dependentesArray.length; k++) {
          somas[k + 1] += dependentesArray[k][i];
  
          if (dependentesArray[k][i] === null) {
            let zero = 0;
            temp.push(
              zero.toLocaleString("pt-br", {
                style: "currency",
                currency: "BRL",
              })
            );
          } else {
            temp.push(
              dependentesArray[k][i].toLocaleString("pt-br", {
                style: "currency",
                currency: "BRL",
              })
            );
          }
        }
        finalRows.push(temp);
      }
      const somaTotal = somas
        .reduce((partialSum, a) => partialSum + a, 0)
        .toLocaleString("pt-br", {
          style: "currency",
          currency: "BRL",
        });
      somas.map((value, index) => {
        somas[index] = value.toLocaleString("pt-br", {
          style: "currency",
          currency: "BRL",
        });
      });
      finalRows.push(["Somatório Individual", ...somas]);
  
      return [finalRows, somaTotal];
    }
    const nomes = prepareHeaders();
  
    const [linhas, somaTotal] = prepareRows();
  
    const body = [
      {
        image:
          "data:image/png;base64,",
        alignment: "center",
        fit: [110, 105],
      },
      {
        text: "Sindicato dos Agrônomos, Veterinários e Zootecnistas dos Entes Públicos no Estado da Paraíba",
        fontsize: 13,
        bold: true,
        alignment: "center",
        margin: 5,
      },
      {
        text: "SEDE PRÓPRIA",
        fontsize: 12,
        bold: true,
        alignment: "center",
        margin: 5,
      },
      {
        text: "Rua Cel. Otto Feio Silveira, 105 – Pedro Gondim CEP: 58031-010 – João Pessoa – PB.",
        fontsize: 12,
        bold: true,
        alignment: "center",
        margin: 5,
      },
      {
        text: "CNPJ 02.110.702/0001- 53 – Fone fax: 3244 2788 / 9971 7184",
        fontsize: 12,
        bold: true,
        alignment: "center",
        margin: 5,
      },
      {
        text: "Declaração",
        fontsize: 45,
        bold: true,
        margin: [50, 10, 50, 0],
        alignment: "center",
      },
      {
        text: `Declaramos, para fins de prova junto a Delegacia da Receita Federal da Paraíba, que o associado ${data.name} portador do CPF: ${data.cpf} pagou a UNIMED João Pessoa COM REGISTRO DA OPERADORA NA ANS SOB NÚMERO 32.104-4, situada à Rua marechal Deodoro, 420- Torre-João Pessoa PB, CNPJ 08.680.693/0001-77 através do CONTRATO DE PRESTAÇÃO DE SERVIÇOS MÉDICOS E HOSPITALARES através do PLANO UNIVIDA BÁSICO PLUS I COLETIVO POR ADESÃO COM REGISTRO DO PLANO NA ANS 467.142/12-2  mantido com este sindicato pela assistência médico-hospitalar que lhe foi prestada e à sua família no exercício de ${data.impostoDeRenda.ano} as importâncias abaixo descritas:`,
        fontSize: 10,
        bold: false,
        margin: [50, 5, 50, 8],
        alignment: "center",
      },
      {
        text: "Tabela Imposto de Renda",
        fontsize: 25,
        bold: true,
        margin: [15, 10, 50, 8],
        alignment: "center",
      },
      {
        styles: "table",
        fontSize: 10,
        table: {
          headerRows: 1,
          widths: [47,53,53,53,53,53,53,53,53,53,53,53],
          body: [[{ text: "Meses", style: "tableHeader" }, ...nomes], ...linhas],
        },
        layout: "lightHorizontalLines",
      },
      {
        text: `Soma Total ${somaTotal}`,
        fontsize: 25,
        bold: true,
        margin: [15, 10, 50, 8],
        alignment: "left",
      },
      {
        image:
          "data:image/png;base64,",
        alignment: "center",
        fit: [350, 50],
      },
      {
        text: "Presidente do Sindicato",
        fontsize: 25,
        bold: true,
        alignment: "center",
      },
    ];
    const primeiro_nome = data.name.split(" ");
    const fileName = primeiro_nome[0] + " - Declaração";
  
    const document = {
      pageSize: "A4",
      pageMargins: [15, 30, 15, 40],
      pageOrientation: "portrait"/* "landscape" */,
  
      content: [body],
    };
  
    pdfMake.createPdf(document).download(fileName);
  }
};

export default impostoPDF;
